package com.learnfromexamples.helloworld;

public class LanguageFundamentals {

	/**
	 * 1. Variables 2. Primitive Data Types (boolean, char, byte, short , int, long,
	 * float, double) 3. KeyWords (abstract, final, import, interface, etc) 4.
	 * Variables Types (Local variables (static), Instance Variables, Static Variables) 5.
	 * Variables Scopes
	 * 
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// a
		/* int a = 8 */

		boolean flag = false;

		char c = '4';

		byte b = 127; // (-128 to 127)

		short s = -32768; // -32768 to 32767

		int i = 2147483647; // - 2147483648 to 2147483647

		long l = 9223372036854775807L; // -9223372036854775808 to 9223372036854775807

		float f = 5.5f; // 4 bytes

		double d = 5.6; // 8 bytes

		System.out.println(flag);
		System.out.println(c);
		/*
		 * System.out.println(b); System.out.println(s); System.out.println(i);
		 * System.out.println(l); System.out.println(f); System.out.println(d);
		 */
	}

	
	public void variablesNamingRules() {
		int apple;
		//int 5g;
		int g6h;
		int _grade;
		int $count;
		int empCount;
		//int g^G;
	}

}
